package pattern;

public class trianglepattern {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for(int i=1;i<=9;i++)//no of rows
			{
			
			{
				for(int k=i;k<=8;k++)//no of columns
					{
					
					
					System.out.print(" "); 
				}
				for(int j=1;j<=i;j++)//no of column/space
				{
					
					
					System.out.print(j);
			       System.out.print(" ");
				}
				
			}System.out.println();
			

	}

	}}
